mapper.select:

select userId, userPw, name
  from member
 where userId = #{userId} and userPw = #{userPw}


	select count(*)
	  from member
	 where userId = #{userId, jdbcType=VARCHAR} and userPw = #{userPw, jdbcType=VARCHAR}

mapper.update:

update member
  set name = #{name}, userPw = #{userPw}
 where userId = #{userId}
